// src/App.jsx
import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import VitalsGrid from "./VitalsGrid";
import "./styles.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
});

export default function App() {
  const [vitals, setVitals] = useState({ hr: 0, spo2: 0, temp: 0 });
  const [status, setStatus] = useState("Connecting...");
  const [alertsEnabled, setAlertsEnabled] = useState(true); // ‚úÖ toggle state

  // socket connection
  useEffect(() => {
    socket.on("connect", () => {
      console.log("‚úÖ Connected to backend");
      setStatus("Connected");
    });

    socket.on("disconnect", () => {
      setStatus("Disconnected");
    });

    socket.on("blynk-data", (data) => {
      console.log("üì° Received data:", data);
      setVitals(data);
    });

    return () => socket.disconnect();
  }, []);

  // fetch alert status
  useEffect(() => {
    fetch("http://localhost:5000/alert-status")
      .then((res) => res.json())
      .then((data) => setAlertsEnabled(data.enabled))
      .catch((err) => console.error("Failed to fetch alert status:", err));
  }, []);

  // toggle alert sending
  const toggleAlerts = async () => {
    try {
      const res = await fetch("http://localhost:5000/toggle-alerts", {
        method: "POST",
      });
      const data = await res.json();
      setAlertsEnabled(data.enabled);
    } catch (err) {
      console.error("Failed to toggle alerts:", err);
    }
  };

  // determine if patient is critical
  const critical =
    (vitals.hr && vitals.hr > 120) ||
    (vitals.spo2 && vitals.spo2 < 90) ||
    (vitals.temp && vitals.temp > 38);

  return (
    <div className="app-root">
      <header className="topbar">
        <div className="title">Rakshak ‚Äì Elderly Health Monitor</div>

        {/* ‚úÖ New control section */}
        <div className="top-controls">
          <button
            className={`toggle-btn ${alertsEnabled ? "on" : "off"}`}
            onClick={toggleAlerts}
          >
            {alertsEnabled ? "üîî Alerts ON" : "üîï Alerts OFF"}
          </button>

          <div className={`status ${critical ? "critical" : ""}`}>
            {critical ? "‚ö†Ô∏è ALERT" : status}
          </div>
        </div>
      </header>

      <VitalsGrid vitals={vitals} />

      <footer className="footer">
        ¬© 2025 Rakshak | Real-Time Health Dashboard
      </footer>
    </div>
  );
}
