// src/GaugeWidget.jsx
import React from "react";
import GaugeChart from "react-gauge-chart";

export default function GaugeWidget({ title, value, unit, max = 200, colorStop = "#21d4a5" }) {
  // percent for gauge [0..1]
  const pct = value == null || isNaN(value) ? 0 : Math.min(1, value / max);

  // small display value
  const display = value == null || isNaN(value) ? "—" : value;

  return (
    <div className="gauge-card">
      <div className="gauge-title">{title}</div>
      <div className="gauge-inner">
        <GaugeChart
          id={`g-${title}`}
          nrOfLevels={30}
          arcsLength={[pct, 1 - pct]}
          colors={[colorStop, "#2b2b2b"]}
          percent={pct}
          hideText
          arcPadding={0.02}
          cornerRadius={3}
        />
        <div className="gauge-value">
          <div className="gauge-num">{display} <span className="gauge-unit">{unit}</span></div>
        </div>
      </div>
    </div>
  );
}
