// src/App.jsx
import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import VitalsGrid from "./VitalsGrid";
import "./styles.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
});

export default function App() {
  const [vitals, setVitals] = useState({ hr: 0, spo2: 0, temp: 0, manual: 0 });
  const [status, setStatus] = useState("Connecting...");
  const [alertsEnabled, setAlertsEnabled] = useState(true);

  useEffect(() => {
    socket.on("connect", () => {
      console.log("‚úÖ Connected to backend");
      setStatus("Connected");
    });

    socket.on("disconnect", () => {
      setStatus("Disconnected");
    });

    socket.on("blynk-data", (data) => {
      console.log("üì° Received data:", data);
      setVitals(data);
    });

    return () => socket.disconnect();
  }, []);

  const toggleAlerts = async () => {
    try {
      const res = await fetch("http://localhost:5000/toggle-alerts", { method: "POST" });
      const json = await res.json();
      setAlertsEnabled(json.enabled);
    } catch (err) {
      console.error("‚ùå Failed to toggle alerts:", err);
    }
  };

  const critical =
    (vitals.hr && vitals.hr > 120) ||
    (vitals.spo2 && vitals.spo2 < 90) ||
    (vitals.temp && vitals.temp > 38);

  return (
    <div className="app-root">
      <header className="topbar">
        <div className="title">Rakshak ‚Äì Elderly Health Monitor</div>
        <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
          <button
            onClick={toggleAlerts}
            style={{
              background: alertsEnabled ? "#ff4d4f" : "#333",
              border: "none",
              color: "#fff",
              padding: "8px 16px",
              borderRadius: "8px",
              cursor: "pointer",
              fontWeight: 600,
            }}
          >
            {alertsEnabled ? "üîî Alerts ON" : "‚öôÔ∏è Alerts OFF"}
          </button>

          <div
            className={`status ${critical ? "critical" : ""}`}
            style={{ minWidth: "80px", textAlign: "center" }}
          >
            {critical ? "‚ö†Ô∏è ALERT" : status}
          </div>

          {/* üü¢ Manual Button Live Indicator */}
          <div
            style={{
              background: vitals.manual === 1 ? "#ff4d4f" : "#333",
              padding: "6px 12px",
              borderRadius: "10px",
              color: "#fff",
              fontSize: "13px",
              fontWeight: 600,
            }}
          >
            {vitals.manual === 1 ? "üñ≤Ô∏è Manual Alert ON" : "‚ö™ Manual Alert OFF"}
          </div>
        </div>
      </header>

      <VitalsGrid vitals={vitals} />

      <footer className="footer">¬© 2025 Rakshak | Real-Time Health Dashboard</footer>
    </div>
  );
}
