// src/VitalsGrid.jsx
import React from "react";
import GaugeWidget from "./GaugeWidget";
import MapView from "./MapView";

/*
 props.vitals expected:
 { hr, spo2, temp, lat, lon, speed }
*/

export default function VitalsGrid({ vitals }) {
  const hr = vitals.hr ?? null;
  const spo2 = vitals.spo2 ?? vitals.spo2Value ?? null;
  const temp = vitals.temp ?? null;
  const lat = vitals.lat ?? "—";
  const lon = vitals.lon ?? "—";
  const speed = vitals.speed ?? null;

  // decide color stops for danger states
  const hrColor = hr && hr > 120 ? "#ff4d4f" : "#21d4a5";
  const spo2Color = spo2 && spo2 < 90 ? "#ff4d4f" : "#3b82f6";
  const tempColor = temp && temp > 38 ? "#ff9900" : "#f59e0b";

  return (
    <div className="grid-root">
      <div className="row">
        <GaugeWidget title="Temperature" value={temp} unit="°C" max={50} colorStop={tempColor} />
        <GaugeWidget title="HR" value={hr} unit="bpm" max={200} colorStop={hrColor} />
      </div>

      <div className="row">
        <GaugeWidget title="SpO₂" value={spo2} unit="%" max={100} colorStop={spo2Color} />
        <div className="small-grid">
          <div className="small-card">
            <div className="small-title">Latitude</div>
            <div className="small-value">{lat}</div>
          </div>
          <div className="small-card">
            <div className="small-title">Longitude</div>
            <div className="small-value">{lon}</div>
          </div>
          <div className="small-card">
            <div className="small-title">Speed</div>
            <div className="small-value">{speed ?? "—"}</div>
          </div>
        </div>
      </div>
      <MapView lat={lat} lon={lon} />
    </div>
    
  );
}
