// src/App.jsx
import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import VitalsGrid from "./VitalsGrid";
import "./styles.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
});

export default function App() {
  const [vitals, setVitals] = useState({ hr: 0, spo2: 0, temp: 0 });
  const [status, setStatus] = useState("Connecting...");
  const [alertsEnabled, setAlertsEnabled] = useState(true);

  useEffect(() => {
    socket.on("connect", () => {
      console.log("‚úÖ Connected to backend");
      setStatus("Connected");
    });

    socket.on("disconnect", () => {
      setStatus("Disconnected");
    });

    socket.on("blynk-data", (data) => {
  console.log("üì° Received data:", data);
  setVitals({
    hr: data.hr,
    spo2: data.spo2,
    temp: data.temp,
  });

  // ‚úÖ Update alertsEnabled if backend sends it (sync with V6)
  if (typeof data.alertsEnabled !== "undefined") {
    setAlertsEnabled(data.alertsEnabled);
  }
});


    return () => socket.disconnect();
  }, []);

  // Alert button toggle
  const toggleAlerts = async () => {
    try {
      const res = await fetch("http://localhost:5000/toggle-alerts", {
        method: "POST",
      });
      const json = await res.json();
      setAlertsEnabled(json.enabled);
    } catch (err) {
      console.error("‚ùå Failed to toggle alerts:", err);
    }
  };

  const critical =
    (vitals.hr && vitals.hr > 120) ||
    (vitals.spo2 && vitals.spo2 < 90) ||
    (vitals.temp && vitals.temp > 38);

  return (
    <div className="app-root">
      <header className="topbar">
        <div className="title">Rakshak ‚Äì Elderly Health Monitor</div>
        <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
          <button
            onClick={toggleAlerts}
            style={{
              background: alertsEnabled ? "#ff4d4f" : "#333",
              border: "none",
              color: "#fff",
              padding: "8px 16px",
              borderRadius: "8px",
              cursor: "pointer",
              fontWeight: 600,
            }}
          >
            {alertsEnabled ? "üîî Alerts ON" : "‚öôÔ∏è Alerts OFF"}
          </button>

          <div className={`status ${critical ? "critical" : ""}`}>
            {critical ? "‚ö†Ô∏è ALERT" : status}
          </div>
        </div>
      </header>

      {/* ‚úÖ Pass live data */}
      <VitalsGrid vitals={vitals} />

      <footer className="footer">
        ¬© 2025 Rakshak | Real-Time Health Dashboard
      </footer>
    </div>
  );
}
