// src/VitalsGrid.jsx
import React from "react";
import GaugeWidget from "./GaugeWidget";
import MapView from "./MapView";

export default function VitalsGrid({ vitals }) {
  // Real-time vitals (from props)
  const hr = vitals?.hr ?? 0;
  const spo2 = vitals?.spo2 ?? 0;
  const temp = vitals?.temp ?? 0;

  // Static GPS for now
  const lat = 17.457441;
  const lon = 78.668363;
  const speed = 0.83;

  const hrColor = hr > 120 ? "#ff4d4f" : "#21d4a5";
  const spo2Color = spo2 < 90 ? "#ff4d4f" : "#3b82f6";
  const tempColor = temp > 38 ? "#ff9900" : "#f59e0b";

  return (
    <div className="grid-root">
      <div className="row">
        <GaugeWidget
          title="Temperature"
          value={temp}
          unit="¬∞C"
          max={100}
          colorStop={tempColor}
        />
        <GaugeWidget
          title="HR"
          value={hr}
          unit="bpm"
          max={200}
          colorStop={hrColor}
        />
      </div>

      <div className="row">
        <GaugeWidget
          title="SpO‚ÇÇ"
          value={spo2}
          unit="%"
          max={100}
          colorStop={spo2Color}
        />
        <div className="small-grid">
          <div className="small-card">
            <div className="small-title">Latitude</div>
            <div className="small-value">{lat.toFixed(6)}</div>
          </div>

          <div className="small-card">
            <div className="small-title">Longitude</div>
            <div className="small-value">{lon.toFixed(6)}</div>
          </div>

          <div className="small-card">
            <div className="small-title">Speed</div>
            <div className="small-value">{speed} km/h</div>
          </div>
        </div>
      </div>

      {/* üó∫Ô∏è Mini Map */}
      <div style={{ marginTop: "25px" }}>
        <MapView lat={lat} lon={lon} />
      </div>
    </div>
  );
}
