import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import VitalsGrid from "./VitalsGrid";
import "./styles.css";

const socket = io("http://localhost:5000", {
  transports: ["websocket", "polling"],
  reconnection: true,
});

export default function App() {
  const [vitals, setVitals] = useState({ hr: 0, spo2: 0, temp: 0 });
  const [status, setStatus] = useState("Connecting...");
  const [alertsOn, setAlertsOn] = useState(true);

  useEffect(() => {
    socket.on("connect", () => {
      console.log("âœ… Connected to backend");
      setStatus("Connected");
    });

    socket.on("blynk-data", (data) => {
      console.log("ðŸ“¡ Received:", data);
      setVitals(data);
    });

    socket.on("disconnect", () => {
      console.log("âŒ Disconnected");
      setStatus("Disconnected");
    });

    return () => {
      socket.off("connect");
      socket.off("blynk-data");
      socket.off("disconnect");
    };
  }, []);

  // Toggle alert switch
  const toggleAlerts = async () => {
    const response = await fetch("http://localhost:5000/toggle-alerts", {
      method: "POST",
    });
    const data = await response.json();
    setAlertsOn(data.enabled);
  };

  return (
    <div className="app-root">
      <header className="topbar">
        <div className="title">Rakshak â€“ Elderly Health Monitor</div>
        <div className="top-controls">
          <button
            onClick={toggleAlerts}
            style={{
              padding: "6px 12px",
              borderRadius: "8px",
              background: alertsOn ? "#21d4a5" : "#444",
              color: "white",
              fontWeight: "bold",
              border: "none",
              cursor: "pointer",
              marginRight: "10px",
            }}
          >
            {alertsOn ? "ðŸ”” Alerts ON" : "ðŸš« Alerts OFF"}
          </button>
          <div
            className={`status ${status === "Disconnected" ? "critical" : ""}`}
          >
            {status}
          </div>
        </div>
      </header>

      <VitalsGrid vitals={vitals} />

      <footer className="footer">
        Â© 2025 Rakshak | Real-Time Health Monitor
      </footer>
    </div>
  );
}
