// src/VitalsGrid.jsx
import React from "react";
import GaugeWidget from "./GaugeWidget";
import MapView from "./MapView";

export default function VitalsGrid() {
  // Example static values for now
  const vitals = {
    hr: 128,
    spo2: 97,
    temp: 42.71,
    lat: 17.457441,
    lon: 78.668363,
    speed: 0.83,
  };

  const hrColor = vitals.hr > 120 ? "#ff4d4f" : "#21d4a5";
  const spo2Color = vitals.spo2 < 90 ? "#ff4d4f" : "#3b82f6";
  const tempColor = vitals.temp > 38 ? "#ff9900" : "#f59e0b";

  return (
    <div className="grid-root">
      <div className="row">
        <GaugeWidget
          title="Temperature"
          value={vitals.temp}
          unit="¬∞C"
          max={50}
          colorStop={tempColor}
        />
        <GaugeWidget
          title="HR"
          value={vitals.hr}
          unit="bpm"
          max={200}
          colorStop={hrColor}
        />
      </div>

      <div className="row">
        <GaugeWidget
          title="SpO‚ÇÇ"
          value={vitals.spo2}
          unit="%"
          max={100}
          colorStop={spo2Color}
        />
        <div className="small-grid">
          <div className="small-card">
            <div className="small-title">Latitude</div>
            <div className="small-value">{vitals.lat}</div>
          </div>

          <div className="small-card">
            <div className="small-title">Longitude</div>
            <div className="small-value">{vitals.lon}</div>
          </div>

          <div className="small-card">
            <div className="small-title">Speed</div>
            <div className="small-value">{vitals.speed} km/h</div>
          </div>
        </div>
      </div>

      {/* üó∫Ô∏è Mini Map View */}
      <div style={{ marginTop: "25px" }}>
        <MapView lat={vitals.lat} lon={vitals.lon} />
      </div>
    </div>
  );
}
